#pragma config(Motor,  port1,           mL,            tmotorVex393_HBridge, openLoop, reversed)
#pragma config(Motor,  port2,           mC,            tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port3,           mAR,           tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port4,           mAL,           tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port10,          mR,            tmotorVex393_HBridge, openLoop)
