/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              Autonomous Task                              */
/*                                                                           */
/*  This task is used to control your robot during the autonomous phase of   */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task autonomous()
{
	motor[mL] = 127;
	motor[mR] = 127;
	wait1Msec(2000);
	motor[mL] = 0;
	motor[mR] = 0;

	// Remove this function call once you have "real" code.
	AutonomousCodePlaceholderForTesting();
}

/*---------------------------------------------------------------------------*/
/*                                                                           */
/*                              User Control Task                            */
/*                                                                           */
/*  This task is used to control your robot during the user control phase of */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task usercontrol()
{
	// User control code here, inside the loop

	while (true)
	{

		UserControlCodePlaceholderForTesting();
		motor[mL] = vexRT[Ch1] + vexRT[Ch2];
		motor[mR] = vexRT[Ch1] - vexRT[Ch2];
		//Tank drive system

		if(vexRT[Btn6U] == 1)
		{
			motor[mAR] = 127;
			motor[mAL] = 127;
		}
		if(vexRT[Btn6D] == 1)
		{
			motor[mAR] = -127;
			motor[mAL] = -127;
		}
		if(vexRT[Btn6D] + vexRT[Btn6U] == 0)
		{
			motor[mAR] = 0;
			motor[mAL] = 0;
		}
		//Arm up and down

		if(vexRT[Btn7L] == 1)
		{
			motor[mC] = 127;
		}
		if(vexRT[Btn7D] == 1)
		{
			motor[mC] = -127;
		}
		if(vexRT[Btn7D] + vexRT[Btn7L] == 0)
		{
			motor[mC] = 0;
		}
		//Claw control

	}
}
